export class UserListComponent {
  constructor(userService, formBuilder) {
    this.userService = userService;
    this.formBuilder = formBuilder;
    this.destroy$ = new Subject();
    this.searchSubject = new Subject();
    // Data properties
    this.users = [];
    this.userStats = null;
    this.pagination = null;
    // State properties
    this.loading = false;
    this.submitting = false;
    // Filter properties
    this.filters = {
      page: 1,
      limit: 10,
      sort: '-createdAt'
    };
    this.currentPage = 1;
    this.searchTerm = '';
    // Modal properties
    this.showUserModal = false;
    this.showPackageModal = false;
    this.showDeleteModal = false;
    this.isEditMode = false;
    // Selected data
    this.selectedUser = null;
    this.userToDelete = null;
    this.userForm = this.createUserForm();
    this.setupSearch();
  }
  ngOnInit() {
    this.loadUsers();
    this.loadUserStats();
  }
  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }
  createUserForm() {
    return this.formBuilder.group({
      name: ['', [Validators.required, Validators.maxLength(50)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      phone: [''],
      role: ['user'],
      isActive: [true]
    });
  }
  setupSearch() {
    this.searchSubject.pipe(debounceTime(300), distinctUntilChanged(), takeUntil(this.destroy$)).subscribe(searchTerm => {
      this.filters.search = searchTerm || undefined;
      this.filters.page = 1;
      this.currentPage = 1;
      this.loadUsers();
    });
  }
  loadUsers() {
    this.loading = true;
    this.userService.getUsers(this.filters).pipe(takeUntil(this.destroy$)).subscribe({
      next: response => {
        this.users = response.data;
        this.pagination = {
          total: response.total,
          prev: response.pagination?.prev,
          next: response.pagination?.next
        };
        this.loading = false;
      },
      error: error => {
        console.error('Error loading users:', error);
        this.loading = false;
      }
    });
  }
  loadUserStats() {
    this.userService.getUserStats().pipe(takeUntil(this.destroy$)).subscribe({
      next: response => {
        this.userStats = response.data;
      },
      error: error => {
        console.error('Error loading user stats:', error);
      }
    });
  }
  onSearchChange(event) {
    this.searchSubject.next(event.target.value);
  }
  applyFilters() {
    this.filters.page = 1;
    this.currentPage = 1;
    this.loadUsers();
  }
  resetFilters() {
    this.filters = {
      page: 1,
      limit: 10,
      sort: '-createdAt'
    };
    this.currentPage = 1;
    this.searchTerm = '';
    this.loadUsers();
  }
  goToPage(page) {
    if (page < 1) return;
    this.filters.page = page;
    this.currentPage = page;
    this.loadUsers();
  }
  getPageNumbers() {
    if (!this.pagination) return [];
    const totalPages = Math.ceil(this.pagination.total / this.filters.limit);
    const pages = [];
    const maxVisible = 5;
    let start = Math.max(1, this.currentPage - Math.floor(maxVisible / 2));
    let end = Math.min(totalPages, start + maxVisible - 1);
    if (end - start + 1 < maxVisible) {
      start = Math.max(1, end - maxVisible + 1);
    }
    for (let i = start; i <= end; i++) {
      pages.push(i);
    }
    return pages;
  }
  // Modal methods
  openCreateModal() {
    this.isEditMode = false;
    this.userForm = this.createUserForm();
    this.showUserModal = true;
  }
  openEditModal(user) {
    this.isEditMode = true;
    this.selectedUser = user;
    this.userForm = this.formBuilder.group({
      name: [user.name, [Validators.required, Validators.maxLength(50)]],
      email: [user.email, [Validators.required, Validators.email]],
      phone: [user.phone || ''],
      role: [user.role],
      isActive: [user.isActive]
    });
    this.showUserModal = true;
  }
  closeUserModal() {
    this.showUserModal = false;
    this.selectedUser = null;
    this.userForm.reset();
  }
  openPackageModal(user) {
    this.selectedUser = user;
    this.showPackageModal = true;
  }
  closePackageModal() {
    this.showPackageModal = false;
    this.selectedUser = null;
  }
  confirmDelete(user) {
    this.userToDelete = user;
    this.showDeleteModal = true;
  }
  closeDeleteModal() {
    this.showDeleteModal = false;
    this.userToDelete = null;
  }
  closeAllModals() {
    this.closeUserModal();
    this.closePackageModal();
    this.closeDeleteModal();
  }
  // CRUD operations
  saveUser() {
    if (this.userForm.invalid) return;
    this.submitting = true;
    const userData = this.userForm.value;
    const operation$ = this.isEditMode ? this.userService.updateUser(this.selectedUser._id, userData) : this.userService.createUser(userData);
    operation$.pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.submitting = false;
        this.closeUserModal();
        this.loadUsers();
        this.loadUserStats();
      },
      error: error => {
        console.error('Error saving user:', error);
        this.submitting = false;
      }
    });
  }
  deleteUser() {
    if (!this.userToDelete) return;
    this.submitting = true;
    this.userService.deleteUser(this.userToDelete._id).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.submitting = false;
        this.closeDeleteModal();
        this.loadUsers();
        this.loadUserStats();
      },
      error: error => {
        console.error('Error deleting user:', error);
        this.submitting = false;
      }
    });
  }
  toggleUserStatus(userId, isActive) {
    this.userService.toggleUserStatus(userId, isActive).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.loadUsers();
        this.loadUserStats();
      },
      error: error => {
        console.error('Error toggling user status:', error);
      }
    });
  }
  // Package management
  assignLifetimePackage() {
    if (!this.selectedUser) return;
    const packageData = {
      packageId: 'lifetime',
      startDate: new Date()
    };
    this.userService.assignPackage(this.selectedUser._id, packageData).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.closePackageModal();
        this.loadUsers();
      },
      error: error => {
        console.error('Error assigning package:', error);
      }
    });
  }
  assignTimedPackage() {
    if (!this.selectedUser) return;
    const endDate = new Date();
    endDate.setMonth(endDate.getMonth() + 1); // 1 month from now
    const packageData = {
      packageId: 'timed',
      startDate: new Date(),
      customEndDate: endDate
    };
    this.userService.assignPackage(this.selectedUser._id, packageData).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.closePackageModal();
        this.loadUsers();
      },
      error: error => {
        console.error('Error assigning package:', error);
      }
    });
  }
  removePackageFromUser() {
    if (!this.selectedUser) return;
    this.userService.removePackage(this.selectedUser._id).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.closePackageModal();
        this.loadUsers();
      },
      error: error => {
        console.error('Error removing package:', error);
      }
    });
  }
  // Utility methods
  trackByUserId(index, user) {
    return user._id;
  }
  getRoleBadgeClass(role) {
    return role === 'admin' ? 'bg-danger' : 'bg-primary';
  }
  getRoleIcon(role) {
    return role === 'admin' ? 'fas fa-user-shield' : 'fas fa-user';
  }
  getRoleText(role) {
    return role === 'admin' ? 'Quản trị viên' : 'Người dùng';
  }
