import { Component, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogModule, MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';
import { MatCheckboxModule } from '@angular/material/checkbox';
import { User } from '../services/user.service';

@Component({
  selector: 'app-user-dialog',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatDialogModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    MatCheckboxModule
  ],
  template: `
    <div class="p-6">
      <h2 class="text-2xl font-bold mb-6">{{isEdit ? 'Chỉnh sửa người dùng' : 'Thêm người dùng mới'}}</h2>
      
      <form [formGroup]="userForm" (ngSubmit)="onSubmit()">
        <div class="grid grid-cols-1 gap-4">
          <mat-form-field appearance="outline" class="w-full">
            <mat-label>Họ và tên</mat-label>
            <input matInput formControlName="name" placeholder="Nhập họ và tên">
            <mat-error *ngIf="userForm.get('name')?.hasError('required')">
              Vui lòng nhập họ và tên
            </mat-error>
            <mat-error *ngIf="userForm.get('name')?.hasError('maxlength')">
              Tên không được vượt quá 50 ký tự
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline" class="w-full">
            <mat-label>Email</mat-label>
            <input matInput formControlName="email" placeholder="Nhập email" type="email">
            <mat-error *ngIf="userForm.get('email')?.hasError('required')">
              Vui lòng nhập email
            </mat-error>
            <mat-error *ngIf="userForm.get('email')?.hasError('email')">
              Email không hợp lệ
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline" class="w-full" *ngIf="!isEdit">
            <mat-label>Mật khẩu</mat-label>
            <input matInput formControlName="password" placeholder="Nhập mật khẩu" type="password">
            <mat-error *ngIf="userForm.get('password')?.hasError('required')">
              Vui lòng nhập mật khẩu
            </mat-error>
            <mat-error *ngIf="userForm.get('password')?.hasError('minlength')">
              Mật khẩu phải có ít nhất 6 ký tự
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline" class="w-full">
            <mat-label>Vai trò</mat-label>
            <mat-select formControlName="role">
              <mat-option value="user">User</mat-option>
              <mat-option value="admin">Admin</mat-option>
            </mat-select>
            <mat-error *ngIf="userForm.get('role')?.hasError('required')">
              Vui lòng chọn vai trò
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline" class="w-full">
            <mat-label>Số điện thoại</mat-label>
            <input matInput formControlName="phone" placeholder="Nhập số điện thoại">
          </mat-form-field>

          <div class="flex items-center gap-2">
            <mat-checkbox formControlName="isActive" color="primary">
              Kích hoạt tài khoản
            </mat-checkbox>
          </div>
        </div>

        <div class="flex justify-end gap-3 mt-6">
          <button mat-button type="button" (click)="onCancel()">
            Hủy
          </button>
          <button mat-raised-button color="primary" type="submit" [disabled]="userForm.invalid">
            {{isEdit ? 'Cập nhật' : 'Thêm mới'}}
          </button>
        </div>
      </form>
    </div>
  `,
  styles: [`
    :host {
      display: block;
    }

    .mat-mdc-dialog-content {
      max-height: 80vh;
    }
  `]
})
export class UserDialogComponent {
  userForm: FormGroup;
  isEdit: boolean;

  constructor(
    private fb: FormBuilder,
    private dialogRef: MatDialogRef<UserDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: { user?: User }
  ) {
    this.isEdit = !!data.user;
    this.userForm = this.fb.group({
      name: [data.user?.name || '', [Validators.required, Validators.maxLength(50)]],
      email: [data.user?.email || '', [Validators.required, Validators.email]],
      password: ['', this.isEdit ? [] : [Validators.required, Validators.minLength(6)]],
      role: [data.user?.role || 'user', [Validators.required]],
      phone: [data.user?.phone || ''],
      isActive: [data.user?.isActive ?? true]
    });
  }

  onSubmit() {
    if (this.userForm.valid) {
      const formData = this.userForm.value;
      if (this.isEdit && !formData.password) {
        delete formData.password;
      }
      this.dialogRef.close(formData);
    }
  }

  onCancel() {
    this.dialogRef.close();
  }
}
