import { Component, OnInit, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatTableModule, MatTableDataSource } from '@angular/material/table';
import { MatPaginatorModule, MatPaginator } from '@angular/material/paginator';
import { MatSortModule, MatSort } from '@angular/material/sort';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatDialogModule } from '@angular/material/dialog';
import { MatSnackBarModule, MatSnackBar } from '@angular/material/snack-bar';
import { MatChipsModule } from '@angular/material/chips';
import { FormsModule } from '@angular/forms';
import { UserService, User } from '../services/user.service';

@Component({
  selector: 'app-user-list',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatPaginatorModule,
    MatSortModule,
    MatButtonModule,
    MatIconModule,
    MatInputModule,
    MatFormFieldModule,
    MatDialogModule,
    MatSnackBarModule,
    MatChipsModule,
    FormsModule
  ],
  template: `
    <div class="container mx-auto p-6">
      <div class="flex justify-between items-center mb-6">
        <h1 class="text-2xl font-bold text-gray-800">Quản lý người dùng</h1>
        <button mat-raised-button color="primary" class="flex items-center gap-2">
          <mat-icon>add</mat-icon>
          Thêm người dùng
        </button>
      </div>

      <!-- Search and Filter -->
      <div class="bg-white rounded-lg shadow-sm p-4 mb-6">
        <mat-form-field class="w-full">
          <mat-label>Tìm kiếm người dùng</mat-label>
          <input matInput (keyup)="applyFilter($event)" placeholder="Nhập tên, email..." #input>
          <mat-icon matSuffix>search</mat-icon>
        </mat-form-field>
      </div>

      <!-- User Table -->
      <div class="bg-white rounded-lg shadow-sm overflow-hidden">
        <table mat-table [dataSource]="dataSource" matSort class="w-full">
          <!-- Name Column -->
          <ng-container matColumnDef="name">
            <th mat-header-cell *matHeaderCellDef mat-sort-header> Tên </th>
            <td mat-cell *matCellDef="let user"> {{user.name}} </td>
          </ng-container>

          <!-- Email Column -->
          <ng-container matColumnDef="email">
            <th mat-header-cell *matHeaderCellDef mat-sort-header> Email </th>
            <td mat-cell *matCellDef="let user"> {{user.email}} </td>
          </ng-container>

          <!-- Role Column -->
          <ng-container matColumnDef="role">
            <th mat-header-cell *matHeaderCellDef mat-sort-header> Vai trò </th>
            <td mat-cell *matCellDef="let user">
              <mat-chip-listbox>
                <mat-chip [color]="user.role === 'admin' ? 'accent' : 'primary'" selected>
                  {{user.role === 'admin' ? 'Admin' : 'User'}}
                </mat-chip>
              </mat-chip-listbox>
            </td>
          </ng-container>

          <!-- Status Column -->
          <ng-container matColumnDef="status">
            <th mat-header-cell *matHeaderCellDef mat-sort-header> Trạng thái </th>
            <td mat-cell *matCellDef="let user">
              <mat-chip-listbox>
                <mat-chip [color]="user.isActive ? 'primary' : 'warn'" selected>
                  {{user.isActive ? 'Hoạt động' : 'Vô hiệu'}}
                </mat-chip>
              </mat-chip-listbox>
            </td>
          </ng-container>

          <!-- Subscription Column -->
          <ng-container matColumnDef="subscription">
            <th mat-header-cell *matHeaderCellDef> Gói dịch vụ </th>
            <td mat-cell *matCellDef="let user">
              <div *ngIf="user.subscription?.packageId; else noSubscription">
                <span class="font-medium">{{user.subscription.packageId.name}}</span>
                <br>
                <span class="text-sm text-gray-500">
                  {{user.subscription.isLifetime ? 'Vĩnh viễn' : 
                    (user.subscription.endDate | date:'dd/MM/yyyy')}}
                </span>
              </div>
              <ng-template #noSubscription>
                <span class="text-gray-500">Chưa đăng ký</span>
              </ng-template>
            </td>
          </ng-container>

          <!-- Created Date Column -->
          <ng-container matColumnDef="createdAt">
            <th mat-header-cell *matHeaderCellDef mat-sort-header> Ngày tạo </th>
            <td mat-cell *matCellDef="let user"> {{user.createdAt | date:'dd/MM/yyyy'}} </td>
          </ng-container>

          <!-- Actions Column -->
          <ng-container matColumnDef="actions">
            <th mat-header-cell *matHeaderCellDef> Thao tác </th>
            <td mat-cell *matCellDef="let user">
              <button mat-icon-button color="primary" (click)="editUser(user)">
                <mat-icon>edit</mat-icon>
              </button>
              <button mat-icon-button color="warn" (click)="deleteUser(user)">
                <mat-icon>delete</mat-icon>
              </button>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
          <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

          <!-- Row shown when no matching data -->
          <tr class="mat-row" *matNoDataRow>
            <td class="mat-cell" colspan="7">
              Không tìm thấy dữ liệu phù hợp với "{{input.value}}"
            </td>
          </tr>
        </table>

        <mat-paginator [pageSizeOptions]="[5, 10, 25, 100]"
                      [length]="totalUsers"
                      [pageSize]="10"
                      (page)="onPageChange($event)"
                      aria-label="Select page of users">
        </mat-paginator>
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: block;
      background-color: #f8f9fa;
      min-height: 100vh;
    }

    .mat-mdc-table {
      width: 100%;
      background: white;
    }

    .mat-mdc-header-cell {
      font-weight: bold;
      color: rgba(0, 0, 0, 0.87);
    }

    .mat-mdc-cell {
      color: rgba(0, 0, 0, 0.87);
    }

    .mat-mdc-row:hover {
      background-color: rgba(0, 0, 0, 0.04);
    }

    .mat-column-actions {
      width: 120px;
      text-align: center;
    }

    .mat-mdc-chip {
      font-size: 12px;
    }
  `]
})
export class UserListComponent implements OnInit {
  displayedColumns: string[] = ['name', 'email', 'role', 'status', 'subscription', 'createdAt', 'actions'];
  dataSource: MatTableDataSource<User>;
  totalUsers = 0;
  currentPage = 1;

  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;

  constructor(
    private userService: UserService,
    private snackBar: MatSnackBar
  ) {
    this.dataSource = new MatTableDataSource<User>([]);
  }

  ngOnInit() {
    this.loadUsers();
  }

  ngAfterViewInit() {
    this.dataSource.sort = this.sort;
  }

  loadUsers(page: number = 1, limit: number = 10) {
    this.userService.getUsers(page, limit).subscribe({
      next: (response) => {
        this.dataSource.data = response.data;
        this.totalUsers = response.total;
      },
      error: (error) => {
        console.error('Error loading users:', error);
        this.snackBar.open('Không thể tải danh sách người dùng', 'Đóng', {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        });
      }
    });
  }

  onPageChange(event: any) {
    this.currentPage = event.pageIndex + 1;
    this.loadUsers(this.currentPage, event.pageSize);
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();

    if (this.dataSource.paginator) {
      this.dataSource.paginator.firstPage();
    }
  }

  editUser(user: User) {
    // Implement edit user logic
    console.log('Edit user:', user);
  }

  deleteUser(user: User) {
    if (confirm('Bạn có chắc chắn muốn xóa người dùng này?')) {
      this.userService.deleteUser(user._id).subscribe({
        next: () => {
          this.loadUsers(this.currentPage);
          this.snackBar.open('Xóa người dùng thành công', 'Đóng', {
            duration: 3000,
            horizontalPosition: 'end',
            verticalPosition: 'top'
          });
        },
        error: (error) => {
          console.error('Error deleting user:', error);
          this.snackBar.open('Không thể xóa người dùng', 'Đóng', {
            duration: 3000,
            horizontalPosition: 'end',
            verticalPosition: 'top'
          });
        }
      });
    }
  }
}
