export class UserListComponent implements OnInit {
  users: User[] = [];
  userStats: UserStats | null = null;
  totalUsers = 0;
  currentPage = 1;
  pageSize = 10;
  searchQuery = '';
  activeFilter = 'all';
  isLoading = false;
  showModal = false;
  editingUser: User | null = null;
  isSubmitting = false;
  userForm: FormGroup;

  constructor(
    private userService: UserService,
    private fb: FormBuilder,
    private toastr: ToastrService
  ) {
    this.userForm = this.fb.group({
      name: ['', [Validators.required, Validators.maxLength(50)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      phone: [''],
      role: ['user', Validators.required],
      isActive: [true]
    });
  }

  ngOnInit(): void {
    this.loadUsers();
    this.loadUserStats();
  }

  loadUsers(): void {
    this.isLoading = true;
    const filters: any = {
      page: this.currentPage,
      limit: this.pageSize,
      sort: '-createdAt'
    };

    if (this.searchQuery) {
      filters.search = this.searchQuery;
    }

    if (this.activeFilter !== 'all') {
      switch (this.activeFilter) {
        case 'active':
          filters.isActive = true;
          break;
        case 'inactive':
          filters.isActive = false;
          break;
        case 'admin':
          filters.role = 'admin';
          break;
      }
    }

    this.userService.getUsers(filters).subscribe({
      next: (response) => {
        this.users = response.data;
        this.totalUsers = response.total;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading users:', error);
        this.toastr.error('Không thể tải danh sách người dùng');
        this.isLoading = false;
      }
    });
  }

  loadUserStats(): void {
    this.userService.getUserStats().subscribe({
      next: (stats) => {
        this.userStats = stats;
      },
      error: (error) => {
        console.error('Error loading user stats:', error);
      }
    });
  }

  onSearch(): void {
    this.currentPage = 1;
    this.loadUsers();
  }

  setFilter(filter: string): void {
    this.activeFilter = filter;
    this.currentPage = 1;
    this.loadUsers();
  }

  onPageChange(page: number): void {
    this.currentPage = page;
    this.loadUsers();
  }

  openAddUserModal(): void {
    this.editingUser = null;
    this.userForm.reset({
      name: '',
      email: '',
      password: '',
      phone: '',
      role: 'user',
      isActive: true
    });
    this.userForm.get('password')?.setValidators([Validators.required, Validators.minLength(6)]);
    this.userForm.get('password')?.updateValueAndValidity();
    this.showModal = true;
  }

  editUser(user: User): void {
    this.editingUser = user;
    this.userForm.patchValue({
      name: user.name,
      email: user.email,
      phone: user.phone || '',
      role: user.role,
      isActive: user.isActive
    });
    this.userForm.get('password')?.clearValidators();
    this.userForm.get('password')?.updateValueAndValidity();
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.editingUser = null;
    this.userForm.reset();
  }

  saveUser(): void {
    if (this.userForm.invalid) {
      this.userForm.markAllAsTouched();
      return;
    }

    this.isSubmitting = true;
    const formData = this.userForm.value;

    if (this.editingUser) {
      // Update user
      const updateData: UpdateUserRequest = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        role: formData.role,
        isActive: formData.isActive
      };

      this.userService.updateUser(this.editingUser._id, updateData).subscribe({
        next: () => {
          this.toastr.success('Cập nhật người dùng thành công');
          this.closeModal();
          this.loadUsers();
          this.loadUserStats();
          this.isSubmitting = false;
        },
        error: (error) => {
          console.error('Error updating user:', error);
          this.toastr.error('Không thể cập nhật người dùng');
          this.isSubmitting = false;
        }
      });
    } else {
      // Create user
      const createData: CreateUserRequest = {
        name: formData.name,
        email: formData.email,
        password: formData.password,
        phone: formData.phone,
        role: formData.role,
        isActive: formData.isActive
      };

      this.userService.createUser(createData).subscribe({
        next: () => {
          this.toastr.success('Thêm người dùng thành công');
          this.closeModal();
          this.loadUsers();
          this.loadUserStats();
          this.isSubmitting = false;
        },
        error: (error) => {
          console.error('Error creating user:', error);
          this.toastr.error('Không thể thêm người dùng');
          this.isSubmitting = false;
        }
      });
    }
  }

  toggleUserStatus(user: User): void {
    const action = user.isActive ? 'khóa' : 'kích hoạt';
    const icon = user.isActive ? 'warning' : 'success';

    Swal.fire({
      title: `Xác nhận ${action}`,
      text: `Bạn có chắc chắn muốn ${action} tài khoản "${user.name}"?`,
      icon: icon,
      showCancelButton: true,
      confirmButtonText: `Đồng ý ${action}`,
      cancelButtonText: 'Hủy'
    }).then((result) => {
      if (result.isConfirmed) {
        this.userService.toggleUserStatus(user._id).subscribe({
          next: () => {
            this.toastr.success(`${action.charAt(0).toUpperCase() + action.slice(1)} tài khoản thành công`);
            this.loadUsers();
            this.loadUserStats();
          },
          error: (error) => {
            console.error('Error toggling user status:', error);
            this.toastr.error(`Không thể ${action} tài khoản`);
          }
        });
      }
    });
  }

  deleteUser(user: User): void {
    Swal.fire({
      title: 'Xác nhận xóa',
      text: `Bạn có chắc chắn muốn xóa người dùng "${user.name}"? Hành động này không thể hoàn tác.`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Xóa',
      cancelButtonText: 'Hủy',
      confirmButtonColor: '#dc3545'
    }).then((result) => {
      if (result.isConfirmed) {
        this.userService.deleteUser(user._id).subscribe({
          next: () => {
            this.toastr.success('Xóa người dùng thành công');
            this.loadUsers();
            this.loadUserStats();
          },
          error: (error) => {
            console.error('Error deleting user:', error);
            this.toastr.error('Không thể xóa người dùng');
          }
        });
      }
    });
  }

  getInitials(name: string): string {
    return name.split(' ').map(word => word[0]).join('').toUpperCase().slice(0, 2);
  }

  formatDate(date: Date | string): string {
    const d = new Date(date);
    return d.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  }
}
