export class UserRolesComponent implements OnInit {
  users: any[] = [];
  displayedColumns: string[] = ['name', 'currentRole', 'roleActions'];
  dataSource!: MatTableDataSource<any>;
  loading = false;

  constructor(
    private userService: UserService,
    private snackBar: MatSnackBar
  ) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.loading = true;
    this.userService.getUsers().subscribe({
      next: (response) => {
        this.users = response.data;
        this.dataSource = new MatTableDataSource(this.users);
        this.loading = false;
      },
      error: (error) => {
        this.snackBar.open('Error loading users', 'Close', { 
          duration: 3000,
          panelClass: ['error-snackbar']
        });
        this.loading = false;
      }
    });
  }

  updateUserRole(userId: string, newRole: string): void {
    this.loading = true;
    this.userService.updateUserRole(userId, newRole).subscribe({
      next: () => {
        this.snackBar.open('User role updated successfully', 'Close', { 
          duration: 3000,
          panelClass: ['success-snackbar']
        });
        this.loadUsers();
      },
      error: (error) => {
        this.snackBar.open('Error updating user role', 'Close', { 
          duration: 3000,
          panelClass: ['error-snackbar']
        });
        this.loading = false;
      }
    });
  }

  getRoleChipClass(role: string): string {
    return `role-chip-${role.toLowerCase()}`;
  }
}
