export class FeatureListComponent {
  constructor(serviceService, authService) {
    this.serviceService = serviceService;
    this.authService = authService;
    this.services = [];
    this.filteredServices = [];
    this.loading = false;
    this.formLoading = false;
    this.error = null;
    this.successMessage = null;
    // Form state
    this.showForm = false;
    this.editMode = false;
    this.selectedService = null;
    // Modal state
    this.showModal = false;
    // Filter state
    this.searchTerm = '';
    this.filterType = '';
    this.filterCategory = '';
    this.filterStatus = '';
    this.serviceTypes = SERVICE_TYPES;
    this.uniqueCategories = [];
  }
  get isAuthenticated() {
    return this.authService.isAuthenticated() && this.authService.isAdmin();
  }
  ngOnInit() {
    this.loadServices();
  }
  loadServices() {
    this.loading = true;
    this.error = null;
    this.serviceService.getServices().subscribe({
      next: services => {
        this.services = services;
        this.updateUniqueCategories();
        this.filterServices();
        this.loading = false;
      },
      error: error => {
        this.error = error.message;
        this.loading = false;
      }
    });
  }
  updateUniqueCategories() {
    this.uniqueCategories = [...new Set(this.services.map(s => s.category))].sort();
  }
  filterServices() {
    let filtered = [...this.services];
    // Search filter
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      filtered = filtered.filter(service => service.name.toLowerCase().includes(term) || service.description && service.description.toLowerCase().includes(term) || service.category.toLowerCase().includes(term) || service.permission.toLowerCase().includes(term));
    }
    // Type filter
    if (this.filterType) {
      filtered = filtered.filter(service => service.type === this.filterType);
    }
    // Category filter
    if (this.filterCategory) {
      filtered = filtered.filter(service => service.category === this.filterCategory);
    }
    // Status filter
    if (this.filterStatus) {
      const isActive = this.filterStatus === 'active';
      filtered = filtered.filter(service => service.isActive === isActive);
    }
    this.filteredServices = filtered;
  }
  showAddForm() {
    this.showForm = true;
    this.editMode = false;
    this.selectedService = null;
  }
  editService(service) {
    this.showForm = true;
    this.editMode = true;
    this.selectedService = service;
  }
  hideForm() {
    this.showForm = false;
    this.editMode = false;
    this.selectedService = null;
    this.clearMessages();
  }
  viewServiceDetails(service) {
    this.selectedService = service;
    this.showModal = true;
  }
  closeModal(event) {
    if (event && event.target !== event.currentTarget) return;
    this.showModal = false;
    this.selectedService = null;
  }
  onFormSubmit(serviceData) {
    this.formLoading = true;
    this.error = null;
    const operation = this.editMode && this.selectedService ? this.serviceService.updateService(this.selectedService._id, serviceData) : this.serviceService.createService(serviceData);
    operation.subscribe({
      next: result => {
        this.formLoading = false;
        this.successMessage = this.editMode ? 'Cập nhật dịch vụ thành công!' : 'Tạo dịch vụ mới thành công!';
        this.hideForm();
        this.loadServices();
        this.clearMessagesAfterDelay();
      },
      error: error => {
        this.formLoading = false;
        this.error = error.message;
      }
    });
  }
  toggleServiceStatus(service) {
    if (confirm(`Bạn có chắc muốn ${service.isActive ? 'vô hiệu hóa' : 'kích hoạt'} dịch vụ "${service.name}"?`)) {
      this.serviceService.toggleServiceStatus(service._id).subscribe({
        next: updatedService => {
          const index = this.services.findIndex(s => s._id === service._id);
          if (index >= 0) {
            this.services[index] = updatedService;
