import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { UserService, User, UserResponse } from '../services/user.service';
import { MatTableModule } from '@angular/material/table';
import { MatPaginatorModule, PageEvent } from '@angular/material/paginator';
import { MatSortModule, Sort } from '@angular/material/sort';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatChipsModule } from '@angular/material/chips';
import { MatDialogModule, MatDialog } from '@angular/material/dialog';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';
import { MatTooltipModule } from '@angular/material/tooltip';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-user-list',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatPaginatorModule,
    MatSortModule,
    MatButtonModule,
    MatIconModule,
    MatChipsModule,
    MatDialogModule,
    MatSnackBarModule,
    MatTooltipModule,
    RouterModule
  ],
  template: `
    <div class="container mx-auto p-6">
      <div class="bg-white rounded-lg shadow-lg p-6">
        <div class="flex justify-between items-center mb-6">
          <h1 class="text-2xl font-bold text-gray-800">Quản lý người dùng</h1>
          <button mat-raised-button color="primary" class="flex items-center" routerLink="../add">
            <mat-icon class="mr-2">add</mat-icon>
            Thêm người dùng
          </button>
        </div>

        <div class="overflow-x-auto">
          <table mat-table [dataSource]="users" matSort (matSortChange)="sortData($event)" 
                 class="w-full">

            <!-- Avatar Column -->
            <ng-container matColumnDef="avatar">
              <th mat-header-cell *matHeaderCellDef class="w-16"> Avatar </th>
              <td mat-cell *matCellDef="let user">
                <img [src]="user.avatar || 'assets/default-avatar.png'" 
                     class="w-10 h-10 rounded-full object-cover"
                     [alt]="user.name">
              </td>
            </ng-container>

            <!-- Name Column -->
            <ng-container matColumnDef="name">
              <th mat-header-cell *matHeaderCellDef mat-sort-header> Tên </th>
              <td mat-cell *matCellDef="let user"> {{user.name}} </td>
            </ng-container>

            <!-- Email Column -->
            <ng-container matColumnDef="email">
              <th mat-header-cell *matHeaderCellDef mat-sort-header> Email </th>
              <td mat-cell *matCellDef="let user"> {{user.email}} </td>
            </ng-container>

            <!-- Role Column -->
            <ng-container matColumnDef="role">
              <th mat-header-cell *matHeaderCellDef mat-sort-header> Vai trò </th>
              <td mat-cell *matCellDef="let user">
                <mat-chip [color]="user.role === 'admin' ? 'accent' : 'primary'" selected>
                  {{user.role === 'admin' ? 'Quản trị viên' : 'Người dùng'}}
                </mat-chip>
              </td>
            </ng-container>

            <!-- Status Column -->
            <ng-container matColumnDef="status">
              <th mat-header-cell *matHeaderCellDef mat-sort-header> Trạng thái </th>
              <td mat-cell *matCellDef="let user">
                <mat-chip [color]="user.isActive ? 'primary' : 'warn'" selected>
                  {{user.isActive ? 'Hoạt động' : 'Đã khóa'}}
                </mat-chip>
              </td>
            </ng-container>

            <!-- Created Date Column -->
            <ng-container matColumnDef="createdAt">
              <th mat-header-cell *matHeaderCellDef mat-sort-header> Ngày tạo </th>
              <td mat-cell *matCellDef="let user"> 
                {{user.createdAt | date:'dd/MM/yyyy'}}
              </td>
            </ng-container>

            <!-- Actions Column -->
            <ng-container matColumnDef="actions">
              <th mat-header-cell *matHeaderCellDef class="w-32"> Thao tác </th>
              <td mat-cell *matCellDef="let user">
                <button mat-icon-button color="primary" 
                        [matTooltip]="'Chỉnh sửa'"
                        [routerLink]="['../edit', user._id]">
                  <mat-icon>edit</mat-icon>
                </button>
                <button mat-icon-button color="warn" 
                        [matTooltip]="'Xóa'"
                        (click)="deleteUser(user)">
                  <mat-icon>delete</mat-icon>
                </button>
                <button mat-icon-button 
                        [color]="user.isActive ? 'warn' : 'primary'"
                        [matTooltip]="user.isActive ? 'Khóa tài khoản' : 'Mở khóa'"
                        (click)="toggleUserStatus(user)">
                  <mat-icon>{{user.isActive ? 'lock' : 'lock_open'}}</mat-icon>
                </button>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"
                class="hover:bg-gray-50 transition-colors duration-150"></tr>
          </table>

          <mat-paginator [length]="totalUsers"
                        [pageSize]="pageSize"
                        [pageSizeOptions]="[5, 10, 25, 100]"
                        (page)="onPageChange($event)"
                        class="mt-4">
          </mat-paginator>
        </div>
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: block;
    }
    .mat-mdc-row:hover {
      background-color: rgba(0, 0, 0, 0.04);
    }
    .mat-column-actions {
      text-align: right;
    }
  `]
})
export class UserListComponent implements OnInit {
  users: User[] = [];
  totalUsers = 0;
  pageSize = 10;
  currentPage = 0;
  displayedColumns: string[] = ['avatar', 'name', 'email', 'role', 'status', 'createdAt', 'actions'];
  sortField = 'createdAt';
  sortDirection = 'desc';

  constructor(
    private userService: UserService,
    private dialog: MatDialog,
    private snackBar: MatSnackBar
  ) {}

  ngOnInit() {
    this.loadUsers();
  }

  loadUsers() {
    const sort = this.sortDirection === 'desc' ? `-${this.sortField}` : this.sortField;
    this.userService.getUsers({
      page: this.currentPage + 1,
      limit: this.pageSize,
      sort: sort
    }).subscribe({
      next: (response: UserResponse) => {
        this.users = response.data;
        this.totalUsers = response.total;
      },
      error: (error) => {
        this.snackBar.open('Không thể tải danh sách người dùng', 'Đóng', {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        });
      }
    });
  }

  onPageChange(event: PageEvent) {
    this.currentPage = event.pageIndex;
    this.pageSize = event.pageSize;
    this.loadUsers();
  }

  sortData(sort: Sort) {
    this.sortField = sort.active;
    this.sortDirection = sort.direction;
    this.loadUsers();
  }

  async deleteUser(user: User) {
    if (confirm(`Bạn có chắc chắn muốn xóa người dùng ${user.name}?`)) {
      try {
        await this.userService.deleteUser(user._id).toPromise();
        this.snackBar.open('Xóa người dùng thành công', 'Đóng', {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        });
        this.loadUsers();
      } catch (error) {
        this.snackBar.open('Không thể xóa người dùng', 'Đóng', {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        });
      }
    }
  }

  async toggleUserStatus(user: User) {
    try {
      await this.userService.updateUser(user._id, { isActive: !user.isActive }).toPromise();
      this.snackBar.open(
        `${user.isActive ? 'Khóa' : 'Mở khóa'} tài khoản thành công`,
        'Đóng',
        {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        }
      );
      this.loadUsers();
    } catch (error) {
      this.snackBar.open(
        `Không thể ${user.isActive ? 'khóa' : 'mở khóa'} tài khoản`,
        'Đóng',
        {
          duration: 3000,
          horizontalPosition: 'end',
          verticalPosition: 'top'
        }
      );
    }
  }
}
