import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../../../environments/environment';
import { MatDialog } from '@angular/material/dialog';

interface PackageFeature {
  name: string;
  description: string;
  isUnlimited: boolean;
  limit?: number;
}

interface Package {
  _id: string;
  name: string;
  description: string;
  type: 'monthly' | 'yearly' | 'lifetime';
  price: number;
  originalPrice?: number;
  currency: 'VND' | 'USD';
  features: PackageFeature[];
  image?: string;
  isActive: boolean;
  isPopular: boolean;
}

@Component({
  selector: 'app-package-list',
  templateUrl: './package-list.component.html',
  styleUrls: ['./package-list.component.scss']
})
export class PackageListComponent implements OnInit {
  packages: Package[] = [];
  displayedColumns: string[] = ['name', 'type', 'price', 'active', 'actions'];
  newPackage: Partial<Package> = {};
  isEditing = false;
  editingPackageId: string | null = null;

  constructor(
    private http: HttpClient,
    private dialog: MatDialog
  ) {}

  ngOnInit() {
    this.fetchPackages();
  }

  fetchPackages() {
    this.http.get<{success: boolean, count: number, data: Package[]}>(
      `${environment.apiUrl}/admin/packages`
    ).subscribe({
      next: (response) => {
        this.packages = response.data;
      },
      error: (err) => {
        console.error('Failed to fetch packages', err);
      }
    });
  }

  addFeature() {
    if (!this.newPackage.features) {
      this.newPackage.features = [];
    }
    this.newPackage.features.push({
      name: '',
      description: '',
      isUnlimited: false,
      limit: 0
    });
  }

  removeFeature(index: number) {
    if (this.newPackage.features) {
      this.newPackage.features.splice(index, 1);
    }
  }

  startEdit(pkg: Package) {
    this.isEditing = true;
    this.editingPackageId = pkg._id;
    this.newPackage = {...pkg};
  }

  cancelEdit() {
    this.isEditing = false;
    this.editingPackageId = null;
    this.newPackage = {};
  }

  submitPackage() {
    if (this.editingPackageId) {
      // Update existing package
      this.http.put<{success: boolean, data: Package}>(
        `${environment.apiUrl}/admin/packages/${this.editingPackageId}`, 
        this.newPackage
      ).subscribe({
        next: () => {
          this.fetchPackages();
          this.cancelEdit();
        },
        error: (err) => {
          console.error('Failed to update package', err);
        }
      });
    } else {
      // Create new package
      this.http.post<{success: boolean, data: Package}>(
        `${environment.apiUrl}/admin/packages`, 
        this.newPackage
      ).subscribe({
        next: () => {
          this.fetchPackages();
          this.newPackage = {};
        },
        error: (err) => {
          console.error('Failed to create package', err);
        }
      });
    }
  }

  deletePackage(id: string) {
    if (confirm('Are you sure you want to delete this package?')) {
      this.http.delete<{success: boolean}>(
        `${environment.apiUrl}/admin/packages/${id}`
      ).subscribe({
        next: () => {
          this.fetchPackages();
        },
        error: (err) => {
          console.error('Failed to delete package', err);
        }
      });
    }
  }

  togglePackageStatus(pkg: Package) {
    this.http.patch<{success: boolean, data: Package}>(
      `${environment.apiUrl}/admin/packages/${pkg._id}/toggle-status`,
      {}
    ).subscribe({
      next: () => {
        this.fetchPackages();
      },
      error: (err) => {
        console.error('Failed to toggle package status', err);
      }
    });
  }
}
