      price: [0, [Validators.required, Validators.min(0)]],
      originalPrice: [null],
      durationValue: [1],
      durationUnit: ['months'],
      isActive: [true],
      isPopular: [false]
    });
  }
  ngOnInit() {
    this.loadPackages();
  }
  loadPackages() {
    this.packageService.getPackages().subscribe({
      next: response => {
        this.dataSource = response.data;
      },
      error: error => {
        this.snackBar.open('Lỗi khi tải danh sách gói', 'Đóng', {
          duration: 3000
        });
      }
    });
  }
  applyFilter(event) {
    const filterValue = event.target.value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }
  filterByType(event) {
    const value = event.value;
    this.dataSource.filter = value;
  }
  filterByStatus(event) {
    const value = event.value;
    this.dataSource.filter = value;
  }
  openPackageDialog(pkg) {
    this.selectedPackage = pkg || null;
    if (pkg) {
      this.packageForm.patchValue({
        name: pkg.name,
        description: pkg.description,
        type: pkg.type,
        price: pkg.price,
        originalPrice: pkg.originalPrice,
        durationValue: pkg.duration?.value,
        durationUnit: pkg.duration?.unit,
        isActive: pkg.isActive,
        isPopular: pkg.isPopular
      });
    } else {
      this.packageForm.reset({
        type: 'monthly',
        isActive: true,
        isPopular: false
      });
    }
    this.dialogRef = this.dialog.open(this.packageDialog, {
      width: '800px'
    });
  }
  onFileSelected(event) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;
    }
  }
  savePackage() {
    if (this.packageForm.valid) {
      const formData = new FormData();
      const formValue = this.packageForm.value;
      Object.keys(formValue).forEach(key => {
        if (key !== 'durationValue' && key !== 'durationUnit') {
          formData.append(key, formValue[key]);
        }
      });
      if (formValue.type !== 'lifetime') {
        formData.append('duration[value]', formValue.durationValue);
        formData.append('duration[unit]', formValue.durationUnit);
      }
      if (this.selectedFile) {
        formData.append('image', this.selectedFile);
      }
      const request = this.selectedPackage ? this.packageService.updatePackage(this.selectedPackage._id, formData) : this.packageService.createPackage(formData);
      request.subscribe({
        next: response => {
          this.snackBar.open(`Gói đã được ${this.selectedPackage ? 'cập nhật' : 'tạo'} thành công`, 'Đóng', {
            duration: 3000
          });
          this.dialogRef.close();
          this.loadPackages();
        },
        error: error => {
          this.snackBar.open(`Lỗi khi ${this.selectedPackage ? 'cập nhật' : 'tạo'} gói`, 'Đóng', {
            duration: 3000
          });
        }
      });
    }
  }
  toggleStatus(pkg) {
    this.packageService.togglePackageStatus(pkg._id).subscribe({
      next: response => {
        this.snackBar.open('Đã cập nhật trạng thái gói', 'Đóng', {
          duration: 3000
        });
        this.loadPackages();
      },
      error: error => {
        this.snackBar.open('Lỗi khi cập nhật trạng thái gói', 'Đóng', {
          duration: 3000
        });
      }
    });
  }
  deletePackage(pkg) {
    if (confirm('Bạn có chắc chắn muốn xóa gói này?')) {
      this.packageService.deletePackage(pkg._id).subscribe({
        next: response => {
          this.snackBar.open('Đã xóa gói thành công', 'Đóng', {
            duration: 3000
          });
          this.loadPackages();
        },
        error: error => {
          this.snackBar.open('Lỗi khi xóa gói', 'Đóng', {
            duration: 3000
          });
        }
      });
    }
  }
